package patterns;

public class Pattern13 {
	/**
	 * 
	 * @param args
	 * 
	 * printing even numbers
	 */

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int k=2;
          for(int i=1;i<5;i++) {
        	  for(int j=1;j<=5;j++) {
        		  System.out.print(k+" ");
        		  k+=2;
        	  }
        	  System.out.println( );
          }
	}

}
