package Strings;

public class StringConcantenationDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str="Sampath";
		
		String str1="kumar";
		
		System.out.println(str+" "+str1);
		
		System.out.println(str.concat(str1));
		
		

	}

}
